SELECT * FROM customer INNER JOIN orders ON orders.o_customerId = customer.c_customerId INNER JOIN order_details ON orders_details.od_orderId = orders.o_orderId INNER JOIN product ON product.p_productId = order_details.od_productId INNER JOIN product_category ON product_category.pc_categoryId = product.p_categoryId INNER JOIN pet ON pet.pt_petId = product_category.pc_petId WHERE p_manufacturer = "Mars Incorporated" AND pt_pet_name = "Cat" AND o_orderDate = "01-03-2021" AND c_cityName = "Almaty" GROUP BY c_customerId HAVING count(*) > 10;
