SELECT p_productName, count(*) FROM product INNER JOIN product_category ON product_category.pc_categoryId = product.p_categoryId INNER JOIN pet ON pet.pt_petId = product_category.pc_petId INNER JOIN order_details ON order_details.p_productId = product.p_productId INNER JOIN orders ON orders.o_orderId = order_details.o_orderId INNER JOIN customer ON orders.o_customerId = customer.c_customerId WHERE pt_petName AND c_cityName = "Almaty" GROUP BY pt_petName;; 
