SELECT * from customer INNER JOIN orders ON orders.o_customerId = customer.c_customerId INNER JOIN order_details ON orders.od_orderId = order_details.od_orderId INNER JOIN product ON orders.o_productId = product.p_productId INNER JOIN product_category ON product.p_categoryName = product_category.pc_name INNER JOIN pet ON product_category.pc_name = pet.pt_petName WHERE (pc_categoryName = "купалка" AND pt_petName = "bird") OR (pc_categoryName = "toys" AND pt_petName = "cat")
